<script lang="ts">
    import { onMount } from "svelte";
    import { writable, type Writable } from "svelte/store";
    import { get } from "svelte/store";
    import { printers } from "./printers.ts";

    const printersS = get(printers);
    // generated random letters then reveals the end name
    function randomLettersEffect(e) {
        const letters = "ABCDEFGHIJKLMNOPQRSTUVWXYZ";

        let interval: number | undefined;
        let iteration = 0;

        const name = e;

        clearInterval(interval);

        interval = setInterval(() => {
            name.innerText = name.innerText
                .split("")
                .map((letter: any, index: number) => {
                    if (index < iteration) {
                        return name.dataset.value[index];
                    }

                    return letters[Math.floor(Math.random() * 26)];
                })
                .join("");

            if (iteration >= name.dataset.value.length) {
                clearInterval(interval);
            }

            iteration += 1 / 3;
        }, 30);
    }

    onMount(() => {
        console.log("Hello from the page component!");
    });
</script>

<section>
    <div class="bg-[#2c2e30] w-full h-[100dvh] items-center">
        <h1
            class="text-[#FFFFFF] text-[50px] font-bold w-[25%] h-24 text-center bg-[#4C5C68] rounded-[10px] flex flex-col items-center justify-center mx-auto shadow-md shadow-[#383a3d]"
        >
            Printers
        </h1>
        <grid
            class="px-12 py-8 bg-[383a3d] w-[100%] h-[85%] grid grid-cols-4 auto-rows-max justify-evenly gap-y-4"
        >
            {#each printersS as printer}
                <div
                    class="flex flex-col items-center justify-center h-[250px] w-[250px] px-4 py-6 shadow-md shadow-[#383a3d] bg-[#383a3d] rounded-md border-2 border-[#4C5C68] transition-all duration-300 ease-in-out"
                    id={printer.apiKey}
                >
                    <!-- make a card to display image, on hover display printer name -->
                    <!-- svelte-ignore a11y-no-static-element-interactions -->
                    <!-- svelte-ignore a11y-click-events-have-key-events -->
                    <div
                        class="cursor-pointer"
                        id="cardContainer"
                        on:mouseenter={(e) => {
                            console.log("mouse entered");
                            printer.cardHovered = true;
                            randomLettersEffect(e.target.lastChild.firstChild);
                        }}
                        on:mouseleave={(e) => {
                            console.log("mouse left");
                            printer.cardHovered = false;
                        }}
                        on:click={() => {
                            window.location.href =
                                "/printer/" + printer.printerID;
                        }}
                    >
                        <div
                            class="flex flex-col items-center justify-center w-[200px] h-[200px] overflow-hidden bg-[#2F3136] rounded-[10px] transition-all duration-300 ease-in-out blur-none"
                        >
                            <p
                                class="text-slate-300 text-[20px] z-30 font-bold absolute transition-all duration-300 ease-in-out text-center text-3xl translate-y-3 uppercase {printer.cardHovered
                                    ? 'opacity-100 visible'
                                    : 'opacity-0 hidden'}"
                                data-value={printer.name}
                            >
                                {printer.name}
                            </p>
                            <div
                                class="backdrop-blur-sm w-full h-full -z-10 opacity-25 blur-sm backdrop-filter rotate-180 inline-block overflow-hidden"
                            >
                                <img
                                    src="data:image/jpeg;base64,"
                                    alt=""
                                    class="invert w-72 h-72"
                                />
                            </div>
                            <img
                                src={printer.image}
                                alt=""
                                class="w-3/4 h-3/4 rounded-[10px] absolute transition-all duration-300 ease-in-out {printer.cardHovered
                                    ? 'opacity-70 filter brightness-75 blur-sm bg-white'
                                    : ''}"
                            />
                            <!-- make text appear on hover -->

                            <!-- add effect when card is hovered -->
                        </div>
                    </div>
                </div>
            {/each}
        </grid>
    </div>
</section>
